import React from "react";
import { useEffect } from "react";
import { useState } from "react";

function Note() {
  const [records , setRecords] = useState([])

  useEffect(()=>{
    fetch("http://localhost:8080/user")
    .then(response => response.json())
    .then(data => setRecords(data))
    .catch(err => console.log(err))
  }, []);
  const [searchTerm, setSearchTerm] = useState('')

  const [pc,setPc] = useState(false);
  const [browser,setBrowser] = useState(false);

  const handleChange=(data)=>{
    if(data=="pc"){
      setPc(!pc);
    }
    if(data=="browser"){
      setBrowser(!browser);
    }
  }
  return (
    <div>
      <div className="row">
      <div className="col s">
      <input type="text" placeholder="Search..." 
      onChange={(Event) =>{
        setSearchTerm(Event.target.value);
      }}></input> </div>
      <div className="col cb">
      <input type="checkbox" value={pc} onChange={()=>handleChange("pc")} /> PC <br />
      <input type="checkbox" value={browser} onChange={()=>handleChange("browser")} /> Web Browser </div>
      </div>
      <div >
        </div>
        {records.filter((list)=> {
          if (searchTerm == "") {
            if(pc==false && browser==false){
              return list
            }
            else if(pc==true && browser==false && list.platform.toLowerCase().includes("PC".toLocaleLowerCase())){
              return list
            }
            else if(pc==false && browser==true && list.platform.toLowerCase().includes("Browser".toLocaleLowerCase())){
              return list
            }
            else if(pc==true && browser==true && list.platform.toLowerCase().includes("PC (Windows), Web Browser".toLocaleLowerCase())){
              return list
            }
          } else if (list.title.toLowerCase().includes(searchTerm.toLocaleLowerCase())) {
            if(pc==false && browser==false){
              return list
            }
            else if(pc==true && browser==false && list.platform.toLowerCase().includes("PC".toLocaleLowerCase())){
              return list
            }
            else if(pc==false && browser==true && list.platform.toLowerCase().includes("Browser".toLocaleLowerCase())){
              return list
            }
            else if(pc==true && browser==true && list.platform.toLowerCase().includes("PC (Windows), Web Browser".toLocaleLowerCase())){
              return list
            }
          }
        }).map((list,index)=> (
          <div>
            <table className="table table.bordered">

            <tbody>
            <tr>
            <th scope="col">{list.id}</th>
            <th scope="col">{list.first_name}</th>
            <th scope="col">{list.last_name}</th>
            <th scope="col">{list.email}</th>
            <th scope="col">{list.gender}</th>
            <th scope="col">{list.income}</th>
            <th scope="col">{list.city}</th>
            <th scope="col">{list.car}</th>
            <th scope="col">{list.quote}</th>
            <th scope="col">{list.phone_price}</th>
            </tr>
            </tbody>
             </table>
          </div>
        ))}
    </div>
  );
}

export default Note;
