import React from "react";
import { useEffect } from "react";
import { useState } from "react";

function Note() {
  const [records , setRecords] = useState([])

  useEffect(()=>{
    fetch('/games')
    .then(response => response.json())
    .then(data => setRecords(data))
    .catch(err => console.log(err))
  }, []);
  const [searchTerm, setSearchTerm] = useState('')

  const [pc,setPc] = useState(false);
  const [browser,setBrowser] = useState(false);

  const handleChange=(data)=>{
    if(data=="pc"){
      setPc(!pc);
    }
    if(data=="browser"){
      setBrowser(!browser);
    }
  }
  return (
    <div>
      <div className="row">
      <div className="col s">
      <input type="text" placeholder="Search..." 
      onChange={(Event) =>{
        setSearchTerm(Event.target.value);
      }}></input> </div>
      <div className="col cb">
      <input type="checkbox" value={pc} onChange={()=>handleChange("pc")} /> PC <br />
      <input type="checkbox" value={browser} onChange={()=>handleChange("browser")} /> Web Browser </div>
      </div>

        {records.filter((list)=> {
          if (searchTerm == "") {
            if(pc==false && browser==false){
              return list
            }
            else if(pc==true && browser==false && list.platform.toLowerCase().includes("PC".toLocaleLowerCase())){
              return list
            }
            else if(pc==false && browser==true && list.platform.toLowerCase().includes("Browser".toLocaleLowerCase())){
              return list
            }
            else if(pc==true && browser==true && list.platform.toLowerCase().includes("PC (Windows), Web Browser".toLocaleLowerCase())){
              return list
            }
          } else if (list.title.toLowerCase().includes(searchTerm.toLocaleLowerCase())) {
            if(pc==false && browser==false){
              return list
            }
            else if(pc==true && browser==false && list.platform.toLowerCase().includes("PC".toLocaleLowerCase())){
              return list
            }
            else if(pc==false && browser==true && list.platform.toLowerCase().includes("Browser".toLocaleLowerCase())){
              return list
            }
            else if(pc==true && browser==true && list.platform.toLowerCase().includes("PC (Windows), Web Browser".toLocaleLowerCase())){
              return list
            }
          }
        }).map((list,index)=> (
          <div className="container" key={index}>
            <table className="colspan-9">
            <h5>Id: {list.genre}  First Name: {list.genre}</h5>
            <h5></h5>
            <h5>Last Name: {list.genre}</h5>
            <h5>Email: {list.genre}</h5>
            <h5>Gender: {list.genre}</h5>
            <h5>Income: {list.genre}</h5>
            <h5>City: {list.genre}</h5>
            <h5>Car: {list.genre}</h5>
            <h5>Quote: {list.genre}</h5>
            <h5>Phone Price: {list.genre}</h5>

            <table class="table">
  <thead>
    <tr>
      <th scope="col">#</th>
      <th scope="col">First</th>
      <th scope="col">Last</th>
      <th scope="col">Handle</th>
    </tr>
  </thead>
  <tbody>
    <tr>
      <th scope="row">1</th>
      <td>Mark</td>
      <td>Otto</td>
      <td>@mdo</td>
    </tr>
    <tr>
      <th scope="row">2</th>
      <td>Jacob</td>
      <td>Thornton</td>
      <td>@fat</td>
    </tr>
    <tr>
      <th scope="row">3</th>
      <td colspan="2">Larry the Bird</td>
      <td>@twitter</td>
    </tr>
  </tbody>
</table>
             </table>
          </div>
        ))}
    </div>
  );
}

export default Note;
