import React from "react";
import { useEffect } from "react";
import { useState } from "react";

function Note() {
  const [records , setRecords] = useState([])

  useEffect(()=>{
    fetch('/')
    .then(response => response.json())
    .then(data => setRecords(data))
    .catch(err => console.log(err))
  }, []);
  const [searchTerm, setSearchTerm] = useState('')

  const [pc,setPc] = useState(false);
  const [browser,setBrowser] = useState(false);

  const handleChange=(data)=>{
    if(data=="pc"){
      setPc(!pc);
    }
    if(data=="browser"){
      setBrowser(!browser);
    }
  }
  return (
    <div>
      <div className="row">
      <div className="col s">
      <input type="text" placeholder="Search..." 
      onChange={(Event) =>{
        setSearchTerm(Event.target.value);
      }}></input> </div>
      <div className="col cb">
      <input type="checkbox" value={pc} onChange={()=>handleChange("pc")} /> PC <br />
      <input type="checkbox" value={browser} onChange={()=>handleChange("browser")} /> Web Browser </div>
      </div>
      <div className="container">
      <table className="table table.bordered" style={{width:"auto"}}>
            <tr>
            <th scope="col">Id</th>
            <th scope="col">First Name</th>
            <th scope="col">Last Name</th>
            <th scope="col">Email</th>
            <th scope="col">Gender</th>
            <th scope="col">Income</th>
            <th scope="col">City</th>
            <th scope="col">Car</th>
            <th scope="col">Quote</th>
            <th scope="col">Phone Price</th>
            </tr>
             </table>
        </div>
        {records.filter((list)=> {
          if (searchTerm == "") {
            if(pc==false && browser==false){
              return list
            }
            else if(pc==true && browser==false && list.platform.toLowerCase().includes("PC".toLocaleLowerCase())){
              return list
            }
            else if(pc==false && browser==true && list.platform.toLowerCase().includes("Browser".toLocaleLowerCase())){
              return list
            }
            else if(pc==true && browser==true && list.platform.toLowerCase().includes("PC (Windows), Web Browser".toLocaleLowerCase())){
              return list
            }
          } else if (list.title.toLowerCase().includes(searchTerm.toLocaleLowerCase())) {
            if(pc==false && browser==false){
              return list
            }
            else if(pc==true && browser==false && list.platform.toLowerCase().includes("PC".toLocaleLowerCase())){
              return list
            }
            else if(pc==false && browser==true && list.platform.toLowerCase().includes("Browser".toLocaleLowerCase())){
              return list
            }
            else if(pc==true && browser==true && list.platform.toLowerCase().includes("PC (Windows), Web Browser".toLocaleLowerCase())){
              return list
            }
          }
        }).map((list,index)=> (
          <div className="container" key={index}>
            <table className="table table.bordered">
            <tr>

            </tr>
             </table>
          </div>
        ))}
    </div>
  );
}

export default Note;
